import React, { useState } from "react";
import Card from "../components/card";
import Search from "../components/search";
import Book from "../components/book";
import API from "../utils/API";

function Main() {
  const [books, setBooks] = useState([])
  const [val, setVal] = useState("")
  const [message, setMessage] = useState("")

  const getBooks = () => {
    API.getBooks(val)
      .then(res =>
        setBooks(res.data)
      ).catch(() => {
        setBooks([])
        setMessage("No Matches")
      });
  };

  const handleInputChange = e => {
    setVal(e.target.value);
  };

  const submitForm = event => {
    event.preventDefault();
    getBooks();
  };

  const saveBook = id => {
    const book = books.find(book => book.id === id);
    API.saveBook({
      googleId: book.id,
      title: book.volumeInfo.title,
      subtitle: book.volumeInfo.subtitle,
      link: book.volumeInfo.infoLink,
      authors: book.volumeInfo.authors,
      description: book.volumeInfo.description,
      image: book.volumeInfo.imageLinks.thumbnail
    }).then(() => getBooks());
  };

  return (
    <div>
      <div className="justify-content-center" >
        <Card title="Search for a Book!">
          <Search handleInputChange={handleInputChange} submitForm={submitForm} val={val} />
        </Card>
      </div>
      <div>
        <Card title="Results">
          {books.length ? (
            <ul className="list-group">
              {books.map(book => (
                <Book
                  key={book.id}
                  book={book}
                  Button={() => (
                    <button onClick={() => saveBook(book.id)} className="btn btn-primary ml-2">
                      Save
                    </button>
                  )} />
              ))}
            </ul>) : (<h2 className="text-center">{message}</h2>)}
        </Card>
      </div>
    </div>
  );
}
export default Main;
