import React, { useState } from "react";
import Card from "../components/card";
import Search from "../components/search";
import Book from "../components/book";
import API from "../utils/API";

function main() {
  const [books, setBooks] = useState([])
  const [val, setVal] = useState("")
  const [message, setMessage] = useState("")


  const handleInputChange = event => {
    const { name, value } = event.target;
    setVal({
      [name]: value
    });
  };

  const getBooks = () => {
    API.getBooks(val)
      .then(res =>
        setBooks(res.data)
      ).catch(() => {
        setBooks([])
        setMessage("No Matches")
      });
  };

  const handleFormSubmit = event => {
    event.preventDefault();
    getBooks();
  };

  handleBookSave = id => {
    const book = books.find(book => book.id === id);

    API.saveBook({
      googleId: book.id,
      title: book.volumeInfo.title,
      subtitle: book.volumeInfo.subtitle,
      link: book.volumeInfo.infoLink,
      authors: book.volumeInfo.authors,
      description: book.volumeInfo.description,
      image: book.volumeInfo.imageLinks.thumbnail
    }).then(() => getBooks());
  };

  return (
    <div>
      <div className="justify-content-center" >
        <Card title="Search for a Book!">
          <Search handleInputChange={handleInputChange} handleFormSubmit={handleFormSubmit} val={val} />
        </Card>
      </div>
      <div>
        <Card title="Results">
          {books.length ? (
            <ul className="list-group">
              {books.map(book => (
                <Book
                  key={book.id}
                  title={book.volumeInfo.title}
                  subtitle={book.volumeInfo.subtitle}
                  link={book.volumeInfo.infoLink}
                  authors={book.volumeInfo.authors.join(", ")}
                  description={book.volumeInfo.description}
                  image={book.volumeInfo.imageLinks.thumbnail}
                  Button={() => (
                    <button onClick={() => handleBookSave(book.id)} className="btn btn-primary ml-2">
                      Save
                    </button>
                  )}
                />
              ))}
            </ul>
          ) : (
              <h2 className="text-center">{message}</h2>
            )}
        </Card>
      </div>
    </div>
  );

}

export default Main;
