import React from "react";

function Form({ q, handleInputChange, handleFormSubmit }) {
  return (
    <form>
      <div className="form-group">
        <label >
          <strong>Book</strong>
        </label>
        <input className="form-control" type="text" value={q} placeholder="Harry Potter" name="q" onChange={handleInputChange}/>
      </div>
      <div className="pull-right">
        <button onClick={handleFormSubmit} type="submit" className="btn btn-lg btn-danger float-right">
          Search
        </button>
      </div>
    </form>
  );
}

export default Form;
