import React from "react";
import "./style.css";

function Book( {book, Button} ) {
  return (
    <li className="list-group-item">
      <h3 className="font-italic">{book.volumeInfo.title}</h3>
      <h5 className="font-italic">{book.volumeInfo.subtitle}</h5>
      <img className="inline-block" src={book.volumeInfo.imageLinks.thumbnail} alt={book.volumeInfo.title} />
      <p className="font-italic small">Written by {book.volumeInfo.authors.join(", ")}</p>
      <p className="inline-block" id="description">{book.volumeInfo.description}</p>
      <a className="btn btn-light" target="_blank" rel="noopener noreferrer" href={book.volumeInfo.infoLink}>View</a>
      <Button />
    </li>
  );
}

export default Book;

