import React from "react";
import './form.css'

function Form({ val, handleInputChange, handleFormSubmit }) {
  return (
    <form >
      <div>
        <input className="form-control" type="text" value={val} placeholder="Harry Potter" name="val" onChange={handleInputChange}/>
      </div>
      <div className="pull-right">
        <button onClick={handleFormSubmit} type="submit" className="btn btn-lg btn-danger float-right">
          Search
        </button>
      </div>
    </form>
  );
}

export default Form;
